import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

function App() {
    const [topic, setTopic] = useState('');
    const [topics, setTopics] = useState('');
    const [posts, setPosts] = useState([]);
    const [selectedPost, setSelectedPost] = useState(null);
    const [loading, setLoading] = useState(false);
    const [message, setMessage] = useState('');

    useEffect(() => {
        fetchPosts();
    }, []);

    const fetchPosts = async () => {
        try {
            const response = await axios.get('http://localhost:5002/api/blog/posts');
            setPosts(response.data);
        } catch (error) {
            console.error('Error fetching posts:', error);
        }
    };

    const generatePost = async (e) => {
        e.preventDefault();
        if (!topic.trim()) return;

        setLoading(true);
        setMessage('');

        try {
            const response = await axios.post('http://localhost:5002/api/blog/generate', {
                topic: topic.trim()
            });

            setMessage(`✅ ${response.data.message}`);
            setTopic('');
            fetchPosts();
        } catch (error) {
            setMessage('❌ Failed to generate post');
        } finally {
            setLoading(false);
        }
    };

    const generateMultiplePosts = async (e) => {
        e.preventDefault();
        const topicList = topics.split('\n').filter(t => t.trim());
        if (topicList.length === 0) return;

        setLoading(true);
        setMessage('');

        try {
            const response = await axios.post('http://localhost:5002/api/blog/generate-bulk', {
                topics: topicList.slice(0, 5) // Limit to 5
            });

            setMessage(`✅ ${response.data.message}`);
            setTopics('');
            fetchPosts();
        } catch (error) {
            setMessage('❌ Failed to generate posts');
        } finally {
            setLoading(false);
        }
    };

    const loadSampleTopics = () => {
        setTopics(`React Best Practices
Node.js Performance
Machine Learning Basics
Cloud Computing
Database Design`);
    };

    return (
        <div className="app">
            <header className="app-header">
                <h1>AI Blog Generator</h1>
                <p>Create professional blog posts with AI</p>
            </header>

            <main className="app-main">
                {/* Single Post Generator */}
                <section className="generator-section">
                    <h2>Create Single Post</h2>
                    <form onSubmit={generatePost} className="post-form">
                        <input
                            type="text"
                            value={topic}
                            onChange={(e) => setTopic(e.target.value)}
                            placeholder="Enter blog topic..."
                            className="form-input"
                            disabled={loading}
                        />
                        <button
                            type="submit"
                            className="btn btn-primary"
                            disabled={loading || !topic.trim()}
                        >
                            {loading ? 'Generating...' : 'Generate Post'}
                        </button>
                    </form>
                </section>

                {/* Bulk Generator */}
                <section className="generator-section">
                    <h2>Bulk Generate Posts</h2>
                    <form onSubmit={generateMultiplePosts} className="post-form">
                        <textarea
                            value={topics}
                            onChange={(e) => setTopics(e.target.value)}
                            placeholder="Enter topics, one per line..."
                            rows="4"
                            className="form-textarea"
                            disabled={loading}
                        />
                        <div className="form-actions">
                            <button
                                type="submit"
                                className="btn btn-primary"
                                disabled={loading || !topics.trim()}
                            >
                                {loading ? 'Generating...' : 'Generate All'}
                            </button>
                            <button
                                type="button"
                                className="btn btn-secondary"
                                onClick={loadSampleTopics}
                            >
                                Load Samples
                            </button>
                        </div>
                    </form>
                </section>

                {/* Status Message */}
                {message && (
                    <div className={`message ${message.includes('✅') ? 'success' : 'error'}`}>
                        {message}
                    </div>
                )}

                {/* Posts List */}
                <section className="posts-section">
                    <div className="section-header">
                        <h2>Generated Posts</h2>
                        <span className="post-count">{posts.length} posts</span>
                    </div>

                    {posts.length === 0 ? (
                        <div className="empty-state">
                            <p>No posts generated yet. Create your first post above!</p>
                        </div>
                    ) : (
                        <div className="posts-grid">
                            {posts.map((post) => (
                                <article key={post._id} className="post-card">
                                    <h3>{post.title}</h3>
                                    <div className="post-meta">
                                        <span>{post.wordCount} words</span>
                                        <span>{new Date(post.generatedAt).toLocaleDateString()}</span>
                                    </div>
                                    <p className="post-excerpt">
                                        {post.content.substring(0, 150)}...
                                    </p>
                                    <button
                                        onClick={() => setSelectedPost(post)}
                                        className="btn btn-outline"
                                    >
                                        Read Full Post
                                    </button>
                                </article>
                            ))}
                        </div>
                    )}
                </section>
            </main>

            {/* Post Modal */}
            {selectedPost && (
                <div className="modal" onClick={() => setSelectedPost(null)}>
                    <div className="modal-content" onClick={(e) => e.stopPropagation()}>
                        <div className="modal-header">
                            <h2>{selectedPost.title}</h2>
                            <button
                                className="modal-close"
                                onClick={() => setSelectedPost(null)}
                            >
                                ×
                            </button>
                        </div>
                        <div className="modal-body">
                            <div className="post-content">
                                {selectedPost.content}
                            </div>
                        </div>
                        <div className="modal-footer">
                            <div className="post-stats">
                                <span>{selectedPost.wordCount} words</span>
                                <span>Generated on {new Date(selectedPost.generatedAt).toLocaleString()}</span>
                            </div>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
}

export default App;
